import * as React from 'react';
import {
  Grid,
  Typography,
  TextField,
  Input,
  Button,
  Card,
  CardContent,
} from '@material-ui/core';
import useRestClient from '../../hooks/useRestClient';
import useVisitorSearchReducer from '../../hooks/useVisitorSearchReducer';
import useTranslations from '../../hooks/useTranslations';
import Table from '../Table';
import StatsCard from '../StatsCard';
import useStyles from './style';

const CancleVisit: React.FunctionComponent = () => {
  const reducer = useVisitorSearchReducer();
  const { client } = useRestClient();
  if (!reducer) return null;

  const { state, dispatch } = reducer;

  async function getVisitorsByFilter() {
    const res = await client.getBesucherByFilter({
      name: state.search.name,
      vorname: state.search.firstName,
      skip: state.skip,
      take: state.limit,
      startzeit: state.search.startDate,
      endzeit: state.search.endDate,
    });
    if (res)
      dispatch({
        type: 'setVisitors',
        payload: { rows: res, count: res.length },
      });
  }

  React.useEffect(() => {
    getVisitorsByFilter();
  }, []);

  React.useEffect(() => {}, [state.page, state.limit]);

  const classes = useStyles();
  const { t } = useTranslations();
  return (
    <div className={classes.root}>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <Typography variant="body1">{t('administration')}</Typography>
          <Typography variant="h6">{t('visitor-cancle')}</Typography>
        </Grid>
        <Grid item xs={12} sm={6}></Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6} lg={3} xl={3}>
          <StatsCard title={t('visitor-count-for-period')} value="200" />
        </Grid>
        <Grid item xs={12} sm={6} lg={3} xl={3}>
          <StatsCard
            title={t('visitor-count-current-registered')}
            value={`${state.rows.length}`}
          />
        </Grid>
        <Grid item xs={12} sm={6} lg={3} xl={3} alignItems="center">
          <Card>
            <CardContent>
              <Input
                placeholder={t('search-name')}
                style={{
                  width: '100%',
                }}
                value={state.search.firstName}
                onChange={event =>
                  dispatch({
                    type: 'setSearch',
                    payload: { ...state.search, firstName: event.target.value },
                  })
                }
              />
              <div style={{ marginTop: '8px' }} />
              <Input
                placeholder={t('search-first-name')}
                style={{
                  width: '100%',
                }}
                value={state.search.name}
                onChange={event =>
                  dispatch({
                    type: 'setSearch',
                    payload: { ...state.search, name: event.target.value },
                  })
                }
              />
              <div style={{ marginTop: '8px' }} />
              <Button
                variant="contained"
                color="primary"
                onClick={async () => getVisitorsByFilter()}
              >
                {t('search-apply')}
              </Button>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} lg={3} xl={3} alignItems="center">
          <Card>
            <CardContent>
              <Grid container justify="flex-start">
                <TextField
                  style={{ paddingRight: '12px' }}
                  label={t('search-start-date')}
                  type="datetime-local"
                  defaultValue={state.search.startDate}
                  onChange={event =>
                    dispatch({
                      type: 'setSearch',
                      payload: {
                        ...state.search,
                        startDate: event.target.value,
                      },
                    })
                  }
                />
                <TextField
                  label={t('search-end-date')}
                  type="datetime-local"
                  defaultValue={state.search.endDate}
                  onChange={event => {
                    dispatch({
                      type: 'setSearch',
                      payload: { ...state.search, endDate: event.target.value },
                    });
                  }}
                />
              </Grid>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Table
            rows={state.rows}
            rowsPerPageOptions={[25, 50, 100]}
            count={state.count}
            rowsPerPage={state.limit}
            page={state.page}
            onChangePage={(_: any, newPage: number) => {
              const newSkip =
                newPage > state.page
                  ? state.skip + state.limit
                  : state.skip - state.limit;
              dispatch({
                type: 'changePage',
                payload: { skip: newSkip, page: newPage },
              });
            }}
            onChangeRowsPerPage={event => {
              const limit = Number(event.target.value);
              dispatch({ type: 'changeRowsPerPage', payload: { limit } });
            }}
            displayOnly={false}
            onClickRow={val => console.log(val)}
            buttonLabel="Abmelden"
          />
        </Grid>
      </Grid>
    </div>
  );
};

export default CancleVisit;
