import * as React from 'react';
import clsx from 'clsx';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TablePagination,
  TableRow,
  Toolbar,
  Typography,
  Paper,
  IconButton,
  Checkbox,
  Tooltip,
} from '@material-ui/core';
import useTranslations from '../../hooks/useTranslations';
import DeleteIcon from '@material-ui/icons/Delete';
import FilterListIcon from '@material-ui/icons/FilterList';
import {
  EnhancedTableHeadProps,
  EnhancedTableProps,
  EnhancedTableToolbarProps,
} from '../../types';
import useStyles, { useToolbarStyles } from './style';

function formatValue(val: string | number | Array<any>) {
  if (Array.isArray(val)) return val.join(', ');
  return val;
}

const EnhancedTable: React.FunctionComponent<EnhancedTableProps> = ({
  rows,
  rowsPerPageOptions,
  count,
  rowsPerPage,
  page,
  onChangePage,
  onChangeRowsPerPage,
  uniqueId,
}) => {
  if (rows.length === 0) return null;
  // state
  const [selected, setSelected] = React.useState<Array<string | number>>([]);

  // hooks
  const { t } = useTranslations();
  const classes = useStyles();
  const cells = Object.keys(rows[0]).map(r => {
    return t(r);
  });

  const EnhancedTableHead: React.FunctionComponent<EnhancedTableHeadProps> = ({
    cells,
    numSelected,
    rowCount,
  }) => {
    return (
      <TableHead>
        <TableRow>
          <TableCell padding="checkbox">
            <Checkbox
              indeterminate={numSelected > 0 && numSelected < rowCount}
              checked={rowCount > 0 && numSelected === rowCount}
              onChange={event => {
                if (event.target.checked)
                  setSelected(rows.map(row => row[uniqueId]));
                setSelected([]);
              }}
            />
          </TableCell>
          {cells.map(cell => (
            <TableCell key={cell} align="left" padding="default">
              {cell}
            </TableCell>
          ))}
        </TableRow>
      </TableHead>
    );
  };

  const EnhancedTableToolbar: React.FunctionComponent<EnhancedTableToolbarProps> = ({
    numSelected,
  }) => {
    const classes = useToolbarStyles();
    return (
      <Toolbar
        className={clsx(classes.root, {
          [classes.highlight]: numSelected > 0,
        })}
      >
        {numSelected > 0 ? (
          <Typography
            className={classes.title}
            color="inherit"
            variant="subtitle1"
          >
            {numSelected} ausgewählt
          </Typography>
        ) : (
          <Typography className={classes.title} variant="h6">
            Besuche
          </Typography>
        )}
        {numSelected > 0 ? (
          <Tooltip title="Löschen">
            <IconButton>
              <DeleteIcon />
            </IconButton>
          </Tooltip>
        ) : (
          <Tooltip title="Filter Liste">
            <IconButton>
              <FilterListIcon />
            </IconButton>
          </Tooltip>
        )}
      </Toolbar>
    );
  };

  const EnhancedTableCell: React.FunctionComponent = ({ children }) => {
    const classes = useStyles();
    return (
      <TableCell
        className={classes.cell}
        component="th"
        scope="row"
        padding="none"
      >
        {children}
      </TableCell>
    );
  };

  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <EnhancedTableToolbar numSelected={selected.length} />
        <TableContainer>
          <Table className={classes.table}>
            <EnhancedTableHead
              cells={cells}
              numSelected={selected.length}
              rowCount={rowsPerPage}
            />
            <TableBody>
              {rows.map((row, index) => {
                const keys = Object.keys(row);
                const checked = selected.indexOf(row[uniqueId]) !== -1;
                return (
                  <TableRow
                    hover
                    role="checkbox"
                    tabIndex={-1}
                    key={index}
                    onClick={() => {
                      const id = row[uniqueId];
                      const selectedIndex = selected.indexOf(id);
                      let newSelected: Array<string | number> = [];
                      if (selectedIndex === -1)
                        newSelected = newSelected.concat(selected, id);
                      else if (selectedIndex === 0)
                        newSelected = newSelected.concat(selected.slice(1));
                      else if (selectedIndex === selected.length - 1)
                        newSelected = newSelected.concat(selected.slice(0, -1));
                      else if (selectedIndex > 0) {
                        newSelected = newSelected.concat(
                          selected.slice(0, selectedIndex),
                          selected.slice(selectedIndex + 1)
                        );
                      }
                      setSelected(newSelected);
                    }}
                  >
                    <TableCell padding="checkbox">
                      <Checkbox checked={checked} />
                    </TableCell>
                    {keys.map((k, index) => {
                      // @ts-ignore
                      const value = formatValue(row[k]);
                      return (
                        <EnhancedTableCell key={index}>
                          {value}
                        </EnhancedTableCell>
                      );
                    })}
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={rowsPerPageOptions}
          labelRowsPerPage="Datensätze pro Seite"
          count={count}
          rowsPerPage={rowsPerPage}
          page={page}
          component="div"
          onChangePage={onChangePage}
          onChangeRowsPerPage={onChangeRowsPerPage}
          labelDisplayedRows={({ from, to, count }) =>
            `${from}-${to} von ${count}`
          }
        />
      </Paper>
    </div>
  );
};

export default EnhancedTable;
