import * as React from 'react';
import {
  Grid,
  Typography,
  Paper,
  Input,
  InputLabel,
  FormControl,
  Select,
  MenuItem,
  Button,
} from '@material-ui/core';
import SelectIcon from '@material-ui/icons/SelectAll';
import Table from '../Table';
import useRestClient from '../../hooks/useRestClient';
import useTableSearch from '../../hooks/useTableSearch';
import useTranslations from '../../hooks/useTranslations';
import useStyles from './style';

const INITIAL_FORM_STATE = {
  firstName: '',
  name: '',
  email: '',
  phone: '',
  building: 0,
  rooms: [],
  permissionGranted: false,
};

const CreateNewUser: React.FunctionComponent<{
  buildings: any;
  onRegister: (props: any) => void;
}> = ({ onRegister, buildings }) => {
  const { t } = useTranslations();
  const classes = useStyles();
  const [formState, setFormState] = React.useState<any>(INITIAL_FORM_STATE);
  const [rooms, setRooms] = React.useState<any>(null);
  return (
    <Paper className={classes.contentContainer}>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <Input
            className={classes.input}
            placeholder={t('register-first-name')}
            value={formState.firstName}
            onChange={event =>
              setFormState({ ...formState, firstName: event.target.value })
            }
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <Input
            className={classes.input}
            placeholder={t('register-name')}
            value={formState.name}
            onChange={event =>
              setFormState({ ...formState, name: event.target.value })
            }
          />
        </Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <Input
            className={classes.input}
            type="email"
            placeholder={t('register-email')}
            value={formState.email}
            onChange={event =>
              setFormState({ ...formState, email: event.target.value })
            }
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <Input
            className={classes.input}
            placeholder={t('register-phone')}
            value={formState.phone}
            onChange={event =>
              setFormState({ ...formState, phone: event.target.value })
            }
          />
        </Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <FormControl style={{ width: '100%' }}>
            <InputLabel>Geb채ude</InputLabel>
            <Select
              value={formState.building}
              onChange={event => {
                setFormState({
                  ...formState,
                  building: event.target.value,
                });
                const rooms = buildings
                  .filter((b: any) => b.id === event.target.value)
                  .map((v: any) => v.raeume);
                if (rooms) setRooms(rooms[0]);
              }}
            >
              {buildings &&
                buildings.length > 0 &&
                buildings.map((building: any, index: number) => (
                  <MenuItem key={index} value={building.id}>
                    {building.bezeichnung}
                  </MenuItem>
                ))}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} sm={6}>
          <FormControl style={{ width: '100%' }}>
            <InputLabel>{t('property-rooms')}</InputLabel>
            {rooms && rooms.length > 0 && (
              <Select
                value={formState.rooms}
                onChange={event => {
                  setFormState({
                    ...formState,
                    rooms: event.target.value,
                  });
                }}
                multiple
                input={<Input />}
              >
                {rooms.map((room: any, index: number) => {
                  return (
                    <MenuItem key={index} value={room.id}>
                      {room.bezeichnung}
                    </MenuItem>
                  );
                })}
              </Select>
            )}
          </FormControl>
        </Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              onRegister(formState);
              setFormState(INITIAL_FORM_STATE);
            }}
          >
            {t('register')}
          </Button>
        </Grid>
      </Grid>
    </Paper>
  );
};

const RegisterVisit: React.FunctionComponent = () => {
  // hooks
  const classes = useStyles();
  const reducer = useTableSearch<any>();
  const { t } = useTranslations();
  const { client } = useRestClient();
  if (!reducer) return null;

  const { state, dispatch } = reducer;

  async function getBesucherByFilter() {
    const res = await client.getBesucherByFilter({
      skip: state.skip,
      take: state.limit,
    });

    if (res)
      dispatch({
        type: 'setRows',
        payload: {
          rows: res.map((r: any) => ({
            id: r.id,
            vorname: 'Max',
            nachname: 'Mustermann',
            email: 'max@mustermann.de',
          })),
          count: res.length,
        },
      });
  }

  React.useEffect(() => {
    getBesucherByFilter();
  }, []);

  return (
    <div className={classes.root}>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <Typography variant="body1">{t('administration')}</Typography>
          <Typography variant="h6">{t('visitor-register')}</Typography>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Typography variant="body1">Registrieter Besucher</Typography>
          <Typography variant="h6">Besucher ausw채hlen</Typography>
        </Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <CreateNewUser onRegister={() => {}} buildings={[]} />
        </Grid>
        <Grid item xs={12} sm={6}>
          <Table
            rows={state.rows}
            rowsPerPageOptions={[25, 50, 100]}
            count={state.count}
            rowsPerPage={state.limit}
            page={state.page}
            onChangePage={() => {}}
            onChangeRowsPerPage={() => {}}
            uniqueId="id"
            toolbarSettings={{
              title: 'Besucher',
              tooltipSelected: {
                title: 'Besucher ausw채hlen',
                icon: <SelectIcon />,
                onClick: () => {},
              },
            }}
          />
        </Grid>
      </Grid>
    </div>
  );
};

export default RegisterVisit;
