import * as React from 'react';
import moment from 'moment';
import { Grid, Typography } from '@material-ui/core';
import InputIcon from '@material-ui/icons/Input';
import useRestClient from '../../hooks/useRestClient';
import useVisitorSearchReducer from '../../hooks/useVisitorSearchReducer';
import useTranslations from '../../hooks/useTranslations';
import Table from '../Table';
import useStyles from './style';
import { VisitorTableState, GetBesucherByFilterInput } from '../../types';

function mapStateToReqBody(state: VisitorTableState): GetBesucherByFilterInput {
  return {
    skip: state.skip,
    take: state.limit,
    startzeit: state.search.startDate,
    endzeit: state.search.endDate,
  };
}

const CancleVisit: React.FunctionComponent = () => {
  const reducer = useVisitorSearchReducer();
  const { client, setIsLoading, setHasError } = useRestClient();
  if (!reducer) return null;

  const { state, dispatch } = reducer;

  async function getBesucherByFilter() {
    const res = await client.getBesucherByFilter(mapStateToReqBody(state));
    if (res)
      dispatch({
        type: 'setVisitors',
        payload: { rows: res, count: res.length },
      });
  }

  async function setBesuchEndzeitpunkt(ids: Array<string | number>) {
    setIsLoading(true);
    try {
      for (const id of ids) {
        await client.setBesuchEndzeitpunkt({
          besuchId: Number(id),
          endzeit: moment().toISOString(),
        });
      }
      const res = await client.getBesucherByFilter(mapStateToReqBody(state));
      console.log(res);
      if (res)
        dispatch({
          type: 'setVisitors',
          payload: { rows: res, count: res.length },
        });
    } catch (error) {
      setIsLoading(false);
      setHasError(true);
    }
  }

  React.useEffect(() => {
    getBesucherByFilter();
  }, []);

  React.useEffect(() => {
    getBesucherByFilter();
  }, [state.page, state.limit]);

  const classes = useStyles();
  const { t } = useTranslations();
  return (
    <div className={classes.root}>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <Typography variant="body1">{t('administration')}</Typography>
          <Typography variant="h6">{t('visitor-cancle')}</Typography>
        </Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Table
            rows={state.rows}
            rowsPerPageOptions={[25, 50, 100]}
            count={state.count}
            rowsPerPage={state.limit}
            page={state.page}
            onChangePage={(_: any, newPage: number) => {
              const newSkip =
                newPage > state.page
                  ? state.skip + state.limit
                  : state.skip - state.limit;
              dispatch({
                type: 'changePage',
                payload: { skip: newSkip, page: newPage },
              });
            }}
            onChangeRowsPerPage={event => {
              const limit = Number(event.target.value);
              dispatch({ type: 'changeRowsPerPage', payload: { limit } });
            }}
            uniqueId="id"
            toolbarSettings={{
              title: t('visitor-current-registered'),
              tooltipSelected: {
                title: t('visitor-cancle'),
                icon: <InputIcon />,
                onClick: async ids => setBesuchEndzeitpunkt(ids),
              },
            }}
          />
        </Grid>
      </Grid>
    </div>
  );
};

export default CancleVisit;
