import * as React from 'react';
import {
  Grid,
  Typography,
  Card,
  CardHeader,
  CardContent,
  Divider,
  Button,
  Paper,
  Input,
} from '@material-ui/core';
import {
  PropertyProps,
  PropertyCardProps,
  RoomProps,
  EditModalContentProps,
} from '../../types';
import useTranslations from '../../hooks/useTranslations';
import Modal from '../Modal';
import Dialog from '../Dialog';
import useStyles from './style';

const PropertyCard: React.FunctionComponent<PropertyCardProps> = ({
  title,
  onViewClick,
  onEditClick,
  onDeleteClick,
}) => {
  const { t } = useTranslations();
  return (
    <>
      <Grid item xs={12} sm={6}>
        <Card>
          <CardHeader title={title} />
          <Divider />
          <CardContent>
            {onViewClick && (
              <Button onClick={onViewClick}>{t('view-rooms')}</Button>
            )}
            <Button onClick={onEditClick}>{t('edit')}</Button>
            <Button onClick={onDeleteClick}>{t('delete')}</Button>
          </CardContent>
        </Card>
      </Grid>
    </>
  );
};

const EditModalContent: React.FunctionComponent<EditModalContentProps> = ({
  id,
  title,
  onClick,
}) => {
  const classes = useStyles();
  return (
    <Paper className={classes.paper}>
      <Typography variant="body1">
        Bitte geben Sie eine Bezeichnung ein
      </Typography>
      <div style={{ marginTop: '8px' }} />
      <Input
        placeholder="Bezeichnung"
        className={classes.input}
        value={title}
      />
      <div style={{ marginTop: '8px' }} />
      <Button variant="contained" color="primary">
        Speichern
      </Button>
    </Paper>
  );
};

const Property: React.FunctionComponent<{
  properties: Array<PropertyProps>;
}> = ({ properties }) => {
  const [showEditModal, setShowEditModal] = React.useState<boolean>(false);
  const [showDeleteDialog, setShowDeleteDialog] = React.useState<boolean>(
    false
  );
  const [
    activeProperty,
    setActiveProperty,
  ] = React.useState<PropertyProps | null>(null);
  const [activeRoom, setActiveRoom] = React.useState<RoomProps | null>(null);

  const classes = useStyles();
  const { t } = useTranslations();

  return (
    <>
      <Dialog
        open={showDeleteDialog}
        onClose={() => setShowDeleteDialog(false)}
        title="Gebäude 1"
        content={t('are-you-sure-to-delete')}
        onAgree={() => {}}
        onDisagree={() => {}}
      />
      <Modal
        open={showEditModal}
        onClose={() => setShowEditModal(false)}
      ></Modal>
      <div className={classes.root}>
        <Grid container spacing={4}>
          <Grid item xs={12} sm={6}>
            <Typography variant="body1">{t('administration')}</Typography>
            <Typography variant="h6">Gebäude & Räume</Typography>
          </Grid>
        </Grid>
        <Grid container spacing={4}>
          <Grid item xs={12} sm={6}>
            <Grid container spacing={1}>
              {properties.map((property, index) => (
                <PropertyCard
                  key={index}
                  id={property.id}
                  title={property.title}
                  onViewClick={() => {}}
                  onEditClick={() => {
                    setShowEditModal(true);
                    setEditId(property.id);
                  }}
                  onDeleteClick={() => {
                    setShowDeleteDialog(true);
                    setDeleteId(property.id);
                  }}
                />
              ))}
            </Grid>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Grid container spacing={1}>
              <PropertyCard
                id="1"
                title="Raum 1"
                onEditClick={() => setShowEditModal(true)}
                onDeleteClick={() => setShowDeleteDialog(true)}
              />
            </Grid>
          </Grid>
        </Grid>
      </div>
    </>
  );
};

export default Property;
