import * as React from 'react';
import {
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TablePagination,
  TableRow,
  Toolbar,
  Typography,
  Paper,
  Button,
} from '@material-ui/core';
import { VisitorTableRowProps, EnhancedTableProps } from '../../types';
import useStyles, { useToolbarStyles } from './style';

type EnhancedTableHeadProps = {
  onSelectAllClick: (
    event: React.ChangeEvent<HTMLInputElement>,
    checked: boolean
  ) => void;
  numSelected: number;
  rowCount: number;
};

const cells: Array<Pick<VisitorTableRowProps, 'id'> & {
  key: string | null;
}> = [
  {
    id: 'ID',
    key: 'id',
  },
  {
    id: 'Beginn',
    key: 'startDate',
  },
  {
    id: 'Ende',
    key: 'endDate',
  },
  {
    id: 'Name',
    key: 'name',
  },
  {
    id: 'Vorname',
    key: 'firstName',
  },
  {
    id: 'E-Mail',
    key: 'email',
  },
  {
    id: 'Räume',
    key: 'rooms',
  },
  {
    id: '',
    key: 'action',
  },
];

const cellKeys = cells.map(cell => cell.key);

const EnhancedTableHead: React.FunctionComponent<EnhancedTableHeadProps> = ({
  onSelectAllClick,
  numSelected,
  rowCount,
}) => {
  return (
    <TableHead>
      <TableRow>
        {cells.map(cell => (
          <TableCell key={cell.id} align="left" padding="default">
            {cell.id}
          </TableCell>
        ))}
      </TableRow>
    </TableHead>
  );
};

const EnhancedTableToolbar: React.FunctionComponent = () => {
  const classes = useToolbarStyles();
  return (
    <Toolbar className={classes.root}>
      <Typography className={classes.title} variant="h6" id="tableTitle">
        Besuche
      </Typography>
    </Toolbar>
  );
};

const EnhancedTable: React.FunctionComponent<EnhancedTableProps> = ({
  rows,
}) => {
  const classes = useStyles();
  const [selected, setSelected] = React.useState<Array<string>>([]);
  const [page, setPage] = React.useState(0);
  const [rowsPerPage, setRowsPerPage] = React.useState(25);

  return (
    <div className={classes.root}>
      <Paper className={classes.paper}>
        <EnhancedTableToolbar />
        <TableContainer>
          <Table className={classes.table}>
            <EnhancedTableHead
              numSelected={selected.length}
              onSelectAllClick={event => {
                if (event.target.checked) {
                  const newSelecteds = rows.map(n => n.id);
                  setSelected(newSelecteds);
                  return;
                }
                setSelected([]);
              }}
              rowCount={rows.length}
            />
            <TableBody>
              {rows.map((row, index) => {
                const isItemSelected = selected.indexOf(row.id) !== -1;
                const labelId = `enhanced-table-checkbox-${index}`;
                return (
                  <TableRow
                    hover
                    onClick={() => {
                      const selectedIndex = selected.indexOf(row.id);
                      let newSelected: Array<string> = [];

                      if (selectedIndex === -1) {
                        newSelected = newSelected.concat(selected, row.id);
                      } else if (selectedIndex === 0) {
                        newSelected = newSelected.concat(selected.slice(1));
                      } else if (selectedIndex === selected.length - 1) {
                        newSelected = newSelected.concat(selected.slice(0, -1));
                      } else if (selectedIndex > 0) {
                        newSelected = newSelected.concat(
                          selected.slice(0, selectedIndex),
                          selected.slice(selectedIndex + 1)
                        );
                      }

                      setSelected(newSelected);
                    }}
                    role="checkbox"
                    aria-checked={isItemSelected}
                    tabIndex={-1}
                    key={row.id}
                    selected={isItemSelected}
                  >
                    {cellKeys.map((k, index) => {
                      if (k === 'rooms')
                        return (
                          <TableCell
                            key={index}
                            id={labelId}
                            component="th"
                            scope="row"
                            padding="none"
                          >
                            {// @ts-ignore
                            row[k].concat(', ')}
                          </TableCell>
                        );
                      else if (k === 'action')
                        return (
                          <TableCell
                            key={index}
                            id={labelId}
                            component="th"
                            scope="row"
                            padding="none"
                          >
                            <Button variant="outlined" color="secondary">
                              Abmelden
                            </Button>
                          </TableCell>
                        );
                      else
                        return (
                          <TableCell
                            key={index}
                            id={labelId}
                            component="th"
                            scope="row"
                            padding="none"
                          >
                            {
                              // @ts-ignore
                              row[k]
                            }
                          </TableCell>
                        );
                    })}
                  </TableRow>
                );
              })}
            </TableBody>
          </Table>
        </TableContainer>
        <TablePagination
          rowsPerPageOptions={[25, 50, 100]}
          component="div"
          count={rows.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={(_, newPage) => {
            setPage(newPage);
          }}
          onChangeRowsPerPage={event => {
            setRowsPerPage(parseInt(event.target.value, 10));
            setPage(0);
          }}
        />
      </Paper>
    </div>
  );
};

export default EnhancedTable;
