import * as React from 'react';
import clsx from 'clsx';
import {
  Grid,
  Typography,
  Card,
  CardHeader,
  CardContent,
  Divider,
  Button,
  Paper,
  Input,
} from '@material-ui/core';
import {
  PropertyProps,
  PropertyCardProps,
  RoomProps,
  EditModalContentProps,
} from '../../types';
import useTranslations from '../../hooks/useTranslations';
import Modal from '../Modal';
import Dialog from '../Dialog';
import useStyles from './style';

const PropertyCard: React.FunctionComponent<PropertyCardProps> = ({
  title,
  onViewClick,
  onEditClick,
  onDeleteClick,
}) => {
  const { t } = useTranslations();
  return (
    <>
      <Grid item xs={12} sm={6}>
        <Card>
          <CardHeader title={title} />
          <Divider />
          <CardContent>
            {onViewClick && (
              <Button onClick={onViewClick}>{t('view-rooms')}</Button>
            )}
            <Button onClick={onEditClick}>{t('edit')}</Button>
            <Button onClick={onDeleteClick}>{t('delete')}</Button>
          </CardContent>
        </Card>
      </Grid>
    </>
  );
};

const EditModalContent: React.FunctionComponent<EditModalContentProps> = ({
  id,
  title,
  onSaveClick,
  onExitClick,
  isActive,
}) => {
  const classes = useStyles();
  const [value, setValue] = React.useState<string>(title);
  return (
    <Paper
      className={clsx(classes.paper, {
        [classes.activePaper]: isActive,
      })}
    >
      <Typography variant="body1">
        Bitte geben Sie eine Bezeichnung ein
      </Typography>
      <div style={{ marginTop: '8px' }} />
      <Input
        placeholder="Bezeichnung"
        className={classes.input}
        value={value}
        onChange={event => setValue(event.target.value)}
      />
      <div style={{ marginTop: '8px' }} />
      <Grid container justify="space-between">
        <Button
          variant="contained"
          color="primary"
          disabled={value === title}
          onClick={() => onSaveClick({ title: value, id })}
        >
          Speichern
        </Button>
        <Button variant="contained" color="secondary" onClick={onExitClick}>
          Abbrechen
        </Button>
      </Grid>
    </Paper>
  );
};

const Property: React.FunctionComponent<{
  properties: Array<PropertyProps>;
}> = ({ properties }) => {
  const [showEditModal, setShowEditModal] = React.useState<boolean>(false);
  const [showDeleteDialog, setShowDeleteDialog] = React.useState<boolean>(
    false
  );
  const [activeElement, setActiveElement] = React.useState<RoomProps | null>(
    null
  );
  const [activeRooms, setActiveRooms] = React.useState<{
    id: string;
    rooms: Array<RoomProps>;
  } | null>(null);

  const classes = useStyles();
  const { t } = useTranslations();

  return (
    <>
      <Dialog
        open={showDeleteDialog}
        onClose={() => setShowDeleteDialog(false)}
        title="Gebäude 1"
        content={t('are-you-sure-to-delete')}
        onAgree={() => {}}
        onDisagree={() => setShowDeleteDialog(false)}
      />
      <Modal open={showEditModal} onClose={() => setShowEditModal(false)}>
        {activeElement && (
          <EditModalContent
            id={activeElement.id}
            title={activeElement.title}
            onSaveClick={() => {}}
            onExitClick={() => setShowEditModal(false)}
          />
        )}
      </Modal>
      <div className={classes.root}>
        <Grid container spacing={4}>
          <Grid item xs={12} sm={6}>
            <Typography variant="body1">{t('administration')}</Typography>
            <Typography variant="h6">Gebäude & Räume</Typography>
          </Grid>
        </Grid>
        <Grid container spacing={4}>
          <Grid item xs={12} sm={6}>
            <Grid container spacing={1}>
              {properties.map((property, index) => {
                let isActive = false;
                if (activeRooms) isActive = activeRooms.id === property.id;
                return (
                  <PropertyCard
                    isActive={isActive}
                    key={index}
                    id={property.id}
                    title={property.title}
                    onViewClick={() =>
                      setActiveRooms({ id: property.id, rooms: property.rooms })
                    }
                    onEditClick={() => {
                      setShowEditModal(true);
                      setActiveElement({
                        title: property.title,
                        id: property.id,
                      });
                    }}
                    onDeleteClick={() => {
                      setShowDeleteDialog(true);
                      setActiveElement({
                        title: property.title,
                        id: property.id,
                      });
                    }}
                  />
                );
              })}
            </Grid>
          </Grid>
          <Grid item xs={12} sm={6}>
            <Grid container spacing={1}>
              {activeRooms &&
                activeRooms.rooms.map((room, index) => (
                  <PropertyCard
                    key={index}
                    id={room.id}
                    title={room.title}
                    onEditClick={() => {
                      setShowEditModal(true);
                    }}
                    onDeleteClick={() => setShowDeleteDialog(true)}
                  />
                ))}
            </Grid>
          </Grid>
        </Grid>
      </div>
    </>
  );
};

export default Property;
