import * as React from 'react';
import { Grid, Typography, TextField } from '@material-ui/core';
import moment from 'moment';
import Table from '../Table';
import StatsCard from './StatsCard';
import useStyles from './style';

const Dashboard: React.FunctionComponent = () => {
  const [startDate, setStartDate] = React.useState(
    moment().format('YYYY-MM-DDTHH:MM')
  );
  const [endDate, setEndDate] = React.useState(
    moment().format('YYYY-MM-DDTHH:MM')
  );

  const classes = useStyles();
  return (
    <div className={classes.root}>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6}>
          <Typography variant="body1">Dashboard</Typography>
          <Typography variant="h6">Besucher√ºbersicht</Typography>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Grid container justify="flex-start">
            <TextField
              style={{ paddingRight: '12px' }}
              label="Startdatum"
              type="datetime-local"
              defaultValue={startDate}
              onChange={event => setStartDate(event.target.value)}
            />
            <TextField
              label="Enddatum"
              type="datetime-local"
              defaultValue={endDate}
              onChange={event => setEndDate(event.target.value)}
            />
          </Grid>
        </Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12} sm={6} lg={3} xl={3}>
          <StatsCard title="Anzahl Besucher im Zeitraum" value="200" />
        </Grid>
        <Grid item xs={12} sm={6} lg={3} xl={3}>
          <StatsCard title="Anzahl Besucher aktuell" value="3" />
        </Grid>
      </Grid>
      <Grid container spacing={4}>
        <Grid item xs={12}>
          <Table
            rows={[
              {
                id: '1223',
                startDate: (
                  <>
                    20.03.2020 <br /> 12:33 Uhr
                  </>
                ),
                endDate: (
                  <>
                    20.03.2020 <br /> 12:33 Uhr
                  </>
                ),
                name: 'Gilli',
                firstName: 'Stephan',
                email: 'gilli.stephan@gmail.com',
                rooms: ['Raum 4', 'Raum 6'],
              },
              {
                id: '456',
                startDate: (
                  <>
                    20.03.2020 <br /> 12:33 Uhr
                  </>
                ),
                endDate: (
                  <>
                    20.03.2020 <br /> 12:33 Uhr
                  </>
                ),
                name: 'Gilli',
                firstName: 'Stephan',
                email: 'gilli.stephan@gmail.com',
                rooms: ['Raum 1', 'Raum 2'],
              },
            ]}
          />
        </Grid>
      </Grid>
    </div>
  );
};

export default Dashboard;
